apple.forEach(item=>{const itemContainer=document.createElement('div');itemContainer.classList='item-container';const itemLink=document.createElement('a');itemLink.setAttribute('href',`${item.link }`);itemLink.setAttribute('target','_blank');itemLink.textContent=`${item.title }`;itemLink.classList='item-link';itemVotes=document.createElement('p');itemVotes.textContent=`${item.votes }`;itemVotes.classList='item-vote';hr=document.createElement('hr');itemContainer.append(itemLink,itemVotes);document.body.append(itemContainer,hr)});